/*====  AVA DATA =======================================*/
DROP TABLE IF EXISTS ava_data;

CREATE TABLE ava_data(ava_id serial PRIMARY KEY, ava_name varchar(255) NOT NULL, location varchar(800), climate varchar(800), soils varchar(800), topography varchar(800), varietals varchar(800));

